// Combine and deduplicate all feed items
const allItems = $input.all();

// Extract unique titles and content
const seen = new Set();
const uniqueItems = [];

for (const item of allItems) {
  const title = item.json.title || item.json.name || '';
  const normalizedTitle = title.toLowerCase().trim();

  if (!seen.has(normalizedTitle) && title.length > 0) {
    seen.add(normalizedTitle);
    uniqueItems.push({
      title: title,
      link: item.json.link || item.json.url || '',
      description: item.json.contentSnippet || item.json.description || item.json.content || '',
      source: item.json.creator || item.json.author || 'Unknown',
      pubDate: item.json.pubDate || item.json.isoDate || new Date().toISOString()
    });
  }
}

// Sort by date (newest first) and take top 30
const sortedItems = uniqueItems
  .sort((a, b) => new Date(b.pubDate) - new Date(a.pubDate))
  .slice(0, 30);

// Create a summary for the AI
const summary = sortedItems.map((item, i) => 
  `${i + 1}. ${item.title}\n   Source: ${item.source}\n   Link: ${item.link}\n   Summary: ${item.description.substring(0, 200)}...`
).join('\n\n');

return [{
  json: {
    feedSummary: summary,
    itemCount: sortedItems.length,
    items: sortedItems,
    collectedAt: new Date().toISOString()
  }
}];

const response = $input.first().json;
const content = response.choices[0].message.content;
const feedData = $('Process & Deduplicate Feeds').first().json;

// Format the email content
const today = new Date().toLocaleDateString('en-US', { 
  weekday: 'long', 
  year: 'numeric', 
  month: 'long', 
  day: 'numeric' 
});

const emailHtml = `
<!DOCTYPE html>
<html>
<head>
  <style>
    body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif; line-height: 1.6; color: #333; max-width: 800px; margin: 0 auto; padding: 20px; }
    h1 { color: #1a1a1a; border-bottom: 3px solid #0066cc; padding-bottom: 10px; }
    h2 { color: #0066cc; margin-top: 30px; }
    .idea { background: #f8f9fa; border-left: 4px solid #0066cc; padding: 20px; margin: 20px 0; border-radius: 0 8px 8px 0; }
    .meta { font-size: 0.9em; color: #666; margin-top: 10px; }
    .cta { background: #0066cc; color: white; padding: 15px 30px; text-decoration: none; border-radius: 5px; display: inline-block; margin-top: 20px; }
    .stats { background: #e8f4f8; padding: 15px; border-radius: 8px; margin: 20px 0; }
    .footer { margin-top: 40px; padding-top: 20px; border-top: 1px solid #ddd; color: #666; font-size: 0.9em; }
    pre { white-space: pre-wrap; word-wrap: break-word; }
  </style>
</head>
<body>
  <h1>Daily Blog Ideas - ${today}</h1>

  <div class="stats">
    <strong>Research Summary:</strong> Analyzed ${feedData.itemCount} trending articles from AI news, Reddit, Hacker News, and Dev.to
  </div>

  <h2>Today's Blog Ideas</h2>
  <div class="idea">
    <pre>${content}</pre>
  </div>

  <p><strong>To create a blog from any of these ideas:</strong></p>
  <ol>
    <li>Reply to this email with the idea number you want to write about</li>
    <li>Add any specific angles or points you want to include</li>
    <li>The blog creation workflow will automatically generate and publish it</li>
  </ol>

  <div class="footer">
    <p>This email was automatically generated by your n8n Blog Ideas Pipeline.</p>
    <p>Sources: Google News RSS, Reddit (r/MachineLearning, r/reactjs, r/webdev), Hacker News, Dev.to</p>
  </div>
</body>
</html>
`;

const emailText = `Daily Blog Ideas - ${today}\n\nResearch Summary: Analyzed ${feedData.itemCount} trending articles\n\n${content}\n\n---\nTo create a blog, reply to this email with the idea number you want to write about.`;

return [{
  json: {
    emailHtml: emailHtml,
    emailText: emailText,
    subject: `Daily Blog Ideas - ${today}`,
    ideasContent: content,
    itemCount: feedData.itemCount
  }
}];
